mapper.select:
select res.* from warrior_resources res left join (select own_id,res_id from warrior_permissions where type=0) wp on res.res_id = wp.res_id left join warrior_user_role wr on wp.own_id = wr.role_id where wr.user_id = #{roleId} and res.status = 0
and res.type=#{type}
ORDER BY res.sort asc

select res.* from warrior_resources res left join (select own_id,res_id from warrior_permissions where type=1) wp on res.res_id = wp.res_id where wp.own_id = #{userId} and res.status = 0
and res.type=#{type}
ORDER BY res.sort asc
